    <section class="callout  text-dark w-100" style="" id="std3">
        <div class="container px-4 px-lg-5 text-center">
            <div class="row gx-4 gx-lg-5 justify-content-center">
                <div class="col-lg-20" style="height:10rem;">
                    <div class="row">

                        <p class="lead text-center">
                        <h3 style="font-size:2rem;">Take part in</h3>

                        <!-- Button trigger modal -->
                        <button type="button" class="btn btn-primary w-50"
                            style="margin-top:4rem; margin-left:auto; margin-right:auto;" data-bs-toggle="modal"
                            data-bs-target="#myModal">
                            참석여부 체크하기
                        </button>

                        <!-- Modal -->
                        <div class="modal fade" id="myModal" data-bs-backdrop="static" data-bs-keyboard="false"
                            tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                            <div class="modal-dialog">

                                <form method="POST" name="attendeesForm">
                                    <div class="modal-content">
                                        <div class="modal-header">
                                            <h1 class="modal-title fs-5" id="staticBackdropLabel">참석 희망하기</h1>
                                            <button type="button" class="btn-close" data-bs-dismiss="modal"
                                                aria-label="Close"></button>
                                        </div>
                                        <div class="modal-body">
                                            <div>

                                                <div>
                                                    <label
                                                        style="width:6rem; text-align:right; margin-right:1rem;">신랑/신부 :
                                                    </label>
                                                    <select name="part" style="width:15rem;" required>
                                                        <option selected value="신랑측">신랑측</option>
                                                        <option value="신부측">신부측</option>
                                                    </select>
                                                </div>
                                                <br>

                                                <div>
                                                    <label style="width:6rem; text-align:right; margin-right:1rem;">관계 :
                                                    </label>
                                                    <select name="relation" style="width:15rem;" required>
                                                        <option value="직계">직계</option>
                                                        <option value="친인척">친인척</option>
                                                        <option selected value="지인">지인</option>
                                                        <option value="친구">친구</option>
                                                        <option value="학교">학교</option>
                                                        <option value="직장">직장</option>
                                                        <option value="기타">기타</option>
                                                    </select>

                                                </div>
                                                <br>
                                                <div>
                                                    <label style="width:6rem; text-align:right; margin-right:1rem;">성함 :
                                                    </label>
                                                    <input type="text" style="width:15rem;" name="name" required>
                                                </div>
                                                <br>
                                                <div>
                                                    <label style="width:6rem; text-align:right; margin-right:1rem;">식사
                                                        인원 : </label>
                                                    <input type="number" style="width:15rem;" name="num_of_attendees"
                                                        required>
                                                </div>
                                            </div>
                                            <br>
                                            <div class="modal-footer">
                                                <button type="button" class="btn btn-secondary text-white"
                                                    data-bs-dismiss="modal">취소</button>
                                                <button type="button" onclick="postAttendees();"
                                                    class="btn btn-primary">보내기</button>
                                            </div>
                                        </div>
                                </form>
                            </div>
                        </div>

                        </p>

                    </div>
                </div>
            </div>
    </section>

